import React, { Component } from 'react';
import './App.css';
import UserInput from './UserInput/UserInput';
import UserOutput from './UserOutput/UserOutput'

class App extends Component {


  NameChangeHandler = (event) => {
    this.setState(
      {
        userName : event.target.value 
      }
    )
  }
  render() {

    return (
      <div className="App">
        <UserInput onType = {this.NameChangeHandler} value = {this.state.userName}></UserInput>
        <UserOutput userName = {this.state.userName} age = {this.state.age}></UserOutput>
     </div>
    );
  }
}

export default App;
