import React , { Component } from 'react'
import Grid from './Grid'

class Board extends Component {

    state = {
        turn : 'X' ,
        cells : ['','','','','','','','','','']
    }

    toggleTurn = (turn) => {
        if ( turn == 'X' )
            this.setState( {turn : 'O' } )
        else
            this.setState ( {turn : 'X' } )
    }

    onPlay = (key, turn) => {
        if (this.state.cells[key] == '') {
            let updatedCells = [...this.state.cells]
            updatedCells[key] = turn
            this.setState( { cells : updatedCells } )
            this.toggleTurn( turn )
        }
    }

    checkStatus = (cells) => {

        winningStates =[
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [0,3,6],
            [1,4,7],
            [2,5,8],
            [0,4,8],
            [2,4,6]
        ]


    }

    render () {

        let status = <p>Next Move: {this.state.turn}</p>

        switch ( checkStatus(this.state.cells) ) {
             case 'X' : status = <p> Winner is X</p>
             break;
             case 'O' : status = <p> Winner is O</p>
             break;
             case 'T' : status = <p> Everybody Wins</p>
             break;
             default:
        }

        return (
            <div >
                <Grid onPlay = {this.onPlay} turn = {this.state.turn} cells = {this.state.cells}>

                </Grid>
                {status}
            </div>

        )
    }
}

export default Board
